import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormGroup} from "@angular/forms";
import {Router} from "@angular/router";
import {AuthService} from "../../services/auth.service";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css', './../public.component.css']
})
export class LoginComponent implements OnInit {
  form!: FormGroup

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private authService: AuthService) {
  }

  ngOnInit(): void {
    this.form = this.formBuilder.group({
      email: '',
      password: ''
    });
  }

  submit() {
    console.log(this.form.getRawValue());

    this.authService.login(this.form.getRawValue()).subscribe(() => this.router.navigate(['/']));
  }

}
<h1 id="title" class="fade-in-text">Welcome to Ga1ors!</h1>

<main class="form-signin w-100 m-auto fade-in-text">
  <form [formGroup]="form" (submit)="submit()">
    <img class="mb-4 center" src="https://1000logos.net/wp-content/uploads/2021/06/Florida-Gators-logo.png"
         alt="" width="72"
         height="57">
    <h1 class="h3 mb-3 fw-normal">Please sign in</h1>

    <div class="form-floating">
      <input formControlName="email" name="email" type="email" class="form-control mb-2" id="email" placeholder="UF Email address">
      <label for="email">UF Email Address</label>
    </div>

    <div class="form-floating">
      <input formControlName="password" name="password" type="password" class="form-control" id="password" placeholder="Password">
      <label for="password">Password</label>
    </div>


    <button class="w-100 btn btn-lg btn-primary" type="submit">Submit
      
    </button>
    <button routerLink="/register" class="mt-2 w-100 btn btn-lg btn-primary" type="submit">Register</button>
  </form>
</main>
