import {Component, Input, OnInit} from '@angular/core';
import {AuthService} from "../../services/auth.service";
import {User} from "../../interfaces/user";

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent implements OnInit {
  @Input('user') user: User | undefined;

  constructor(
    private authService: AuthService
  ) {
  }

  ngOnInit(): void {
  }

  logout(): void {
    this.authService.logout().subscribe(() => {
      console.log("Successfully logged out");
    });
  }
}
<header class="navbar navbar-dark sticky-top bg-dark flex-md-nowrap p-0 shadow">
  <a class="navbar-brand col-md-3 col-lg-2 me-0 px-3 fs-6" href="#">Ga1ors</a>

  <nav class="my-2 my-md-0 mr-md-3">
    <a routerLink="/profile" class="p-2 text-white text-decoration-none">{{user?.firstName}} {{user?.lastName}}</a>
    <a routerLink="/login" class="p-2 text-white text-decoration-none" (click)="logout()">Sign Out</a>
  </nav>
</header>
