import {Component, OnInit} from '@angular/core';
import {Router} from "@angular/router";
import {AuthService} from "../../services/auth.service";

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css', './../public.component.css']
})

export class RegisterComponent implements OnInit {
  firstName = '';
  lastName = '';
  email = '';
  password = '';
  passwordConfirm = '';

  constructor(
    private router: Router,
    private authService: AuthService) {
  }

  ngOnInit(): void {
  }

  submit(): void {
    //   console.log({
    //     firstName: this.firstName,
    //     lastName: this.lastName,
    //     email: this.email,
    //     password: this.password,
    //     passwordConfirm: this.password,
    // });

    this.authService.register({
      firstName: this.firstName,
      lastName: this.lastName,
      email: this.email,
      password: this.password,
      passwordConfirm: this.password,
    }).subscribe(() => {
      this.router.navigate(['/login'])
    }); //redirect after successful login
  }
}
<h1 id="title" class="fade-in-text">Welcome to Ga1ors!</h1>

<main class="form-signin w-100 m-auto fade-in-text">
  <form (submit)="submit()">
    <img id="logo" class="mb-4 center" src="https://1000logos.net/wp-content/uploads/2021/06/Florida-Gators-logo.png"
         alt="" width="72"
         height="57">
    <h1 class="h3 mb-3 fw-normal">Please register</h1>

    <div class="form-floating">
      <input [(ngModel)]="firstName" name="firstName" type="email" class="form-control mb-2" id="firstName"
             placeholder="First Name">
      <label for="firstName">First Name</label>
    </div>

    <div class="form-floating">
      <input [(ngModel)]="lastName" name="lastName" type="email" class="form-control mb-2" id="lastName"
             placeholder="Last Name">
      <label for="lastName">Last Name</label>
    </div>

    <div class="form-floating">
      <input [(ngModel)]="email" name="email" type="email" class="form-control mb-2" id="email"
             placeholder="UF Email address">
      <label for="email">UF Email Address</label>
    </div>

    <div class="form-floating">
      <input [(ngModel)]="password" name="password" type="password" class="form-control mb-2" id="password"
             placeholder="Password">
      <label for="password">Password</label>
    </div>

    <div class="form-floating">
      <input [(ngModel)]="passwordConfirm" name="passwordConfirm" type="password" class="form-control"
             id="passwordConfirm" placeholder="Password Confirm">
      <label for="passwordConfirm">Password Confirm</label>
    </div>

    <button type ="button" class="w-100 btn btn-lg btn-primary" type="submit">Submit</button>
    <button routerLink="/login" class="mt-2 w-100 btn btn-lg btn-primary" type="submit">Login</button>
  </form>
</main>
